nasal.open:
setprop("/sim/gui/dialogs/performance/cg-percent-mac", sprintf("%4.1f", math.round(getprop("/fdm/jsbsim/inertia/cg-percent-mac"), 0.1)) ~ "% MAC");
setprop("/sim/gui/dialogs/performance/zfwcg-percent-mac", sprintf("%4.1f", math.round(getprop("/fdm/jsbsim/inertia/zfwcg-percent-mac"), 0.1)) ~ "% MAC");

setprop("/sim/gui/dialogs/performance/current-stabilizer-deg-round", sprintf("%4.1f", math.round(getprop("/fdm/jsbsim/fcs-actual/stabilizer/final-deg"), 0.1) * -1) ~ " DEG");

setprop("/sim/gui/dialogs/performance/v1-round", math.round(getprop("/fdm/jsbsim/performance/v1")));
setprop("/sim/gui/dialogs/performance/vr-round", math.round(getprop("/fdm/jsbsim/performance/vr")));
setprop("/sim/gui/dialogs/performance/v2-round", math.round(getprop("/fdm/jsbsim/performance/v2")));
setprop("/sim/gui/dialogs/performance/stabilizer-deg-round", sprintf("%4.1f", math.round(getprop("/fdm/jsbsim/performance/stabilizer-deg"), 0.1)) ~ " DEG");

setprop("/sim/gui/dialogs/performance/flap-retract-round", math.round(getprop("/fdm/jsbsim/performance/flap-retract")));
setprop("/sim/gui/dialogs/performance/slat-retract-round", math.round(getprop("/fdm/jsbsim/performance/slat-retract")));
setprop("/sim/gui/dialogs/performance/min-maneuver-round", math.round(getprop("/fdm/jsbsim/performance/min-maneuver")));

var loop = maketimer(0.1, func() {
	setprop("/sim/gui/dialogs/performance/cg-percent-mac", sprintf("%4.1f", math.round(getprop("/fdm/jsbsim/inertia/cg-percent-mac"), 0.1)) ~ "% MAC");
	setprop("/sim/gui/dialogs/performance/zfwcg-percent-mac", sprintf("%4.1f", math.round(getprop("/fdm/jsbsim/inertia/zfwcg-percent-mac"), 0.1)) ~ "% MAC");

	setprop("/sim/gui/dialogs/performance/current-stabilizer-deg-round", sprintf("%4.1f", math.round(getprop("/fdm/jsbsim/fcs-actual/stabilizer/final-deg"), 0.1) * -1) ~ " DEG");

	setprop("/sim/gui/dialogs/performance/v1-round", math.round(getprop("/fdm/jsbsim/performance/v1")));
	setprop("/sim/gui/dialogs/performance/vr-round", math.round(getprop("/fdm/jsbsim/performance/vr")));
	setprop("/sim/gui/dialogs/performance/v2-round", math.round(getprop("/fdm/jsbsim/performance/v2")));
	setprop("/sim/gui/dialogs/performance/stabilizer-deg-round", sprintf("%4.1f", math.round(getprop("/fdm/jsbsim/performance/stabilizer-deg"), 0.1)) ~ " DEG");

	setprop("/sim/gui/dialogs/performance/flap-retract-round", math.round(getprop("/fdm/jsbsim/performance/flap-retract")));
	setprop("/sim/gui/dialogs/performance/slat-retract-round", math.round(getprop("/fdm/jsbsim/performance/slat-retract")));
	setprop("/sim/gui/dialogs/performance/min-maneuver-round", math.round(getprop("/fdm/jsbsim/performance/min-maneuver")));
});
loop.start();
